import React from "react";
import PropTypes from "prop-types";

const shapes = {
  round: "rounded-[16px]",
};

const variants = {
  outline: {
    indigo_700: "border-indigo-700 border-2 border-solid text-indigo-700",
  },
};

const sizes = {
  xs: "h-[100px] p-1.5 text-base",
  sm: "h-[200px] p-3.5",
};

const TextArea = React.forwardRef(
  (
    {
      className = "",
      name = "",
      placeholder = "",
      children,
      label = "",
      onChange,
      shape,
      variant = "outline",
      size = "sm",
      color = "indigo_700",
      ...restProps
    },
    ref
  ) => {
    return (
      <label
        className={`${className} flex items-center justify-center cursor-text ${(shape && shapes[shape]) || ""} ${
          variants[variant]?.[color] || variants[variant] || ""
        } ${sizes[size] || ""}`}
      >
        {!!label && <span>{label}</span>}
        <textarea
          ref={ref}
          name={name}
          placeholder={placeholder}
          onChange={onChange}
          {...restProps}
        />
      </label>
    );
  }
);

TextArea.propTypes = {
  className: PropTypes.string,
  name: PropTypes.string,
  placeholder: PropTypes.string,
  label: PropTypes.node,
  onChange: PropTypes.func,
  shape: PropTypes.oneOf(["round"]),
  size: PropTypes.oneOf(["xs", "sm"]),
  variant: PropTypes.oneOf(["outline"]),
  color: PropTypes.oneOf(["indigo_700"]),
};

export { TextArea };
