import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { Helmet } from "react-helmet";
import { Button, Input, Heading, SelectBox, TextArea } from "../../components";
import axios from "axios";

const dropDownOptions = [
    { label: "Em aberto", value: "Em aberto" },
    { label: "Em execução", value: "Em execução" },
    { label: "Aguardando aprovação", value: "Aguardando aprovação" },
    { label: "Aguardando peça", value: "Aguardando peça" },
    { label: "Em processo", value: "Em processo" },
    { label: "Recusada", value: "Recusada" },
    { label: "Finalizada", value: "Finalizada" },
    { label: "Em garantia", value: "Em garantia" },
];

export default function EditarOrdemPage() {
    const { numero } = useParams();
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        status: '',
        // outros campos necessários
    });

    useEffect(() => {
        const fetchOrdem = async () => {
            const token = localStorage.getItem('token');
            try {
                const response = await axios.get(`http://localhost:3010/ordem/${numero}`, {
                    headers: {
                        Authorization: `Bearer ${token}`
                    }
                });
                setFormData(response.data);
            } catch (error) {
                console.error('Erro ao buscar ordem:', error);
            }
        };

        fetchOrdem();
    }, [numero]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevState => ({ ...prevState, [name]: value }));
    };

    const handleSelectChange = (selectedOption) => {
        setFormData(prevState => ({ ...prevState, status: selectedOption ? selectedOption.value : '' }));
    };

    const handleSubmit = async () => {
        const token = localStorage.getItem('token');
        try {
            await axios.put(`http://localhost:3010/ordem/${numero}`, formData, {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });
            navigate('/resultado-ordens');
        } catch (error) {
            console.error('Erro ao atualizar ordem:', error);
        }
    };

    return (
        <>
            <Helmet>
                <title>Editar Ordem</title>
                <meta name="description" content="Editar Ordem" />
            </Helmet>
            <div className="flex w-full flex-col items-start bg-gray-300 py-11 md:py-5">
                <div className="container-xs mb-1 flex flex-col items-center gap-[78px] md:gap-[58px] md:p-5 sm:gap-[39px]">
                    <Header className="self-stretch" title="Editar Ordem" />
                    <div className="flex w-[48%] flex-col items-center md:w-full">
                        <Heading size="textlg" as="h2" className="mt-12 uppercase">
                            Editar Ordem
                        </Heading>
                        <div className="mt-8 flex flex-col items-center self-stretch">
                            <Heading as="h3">Status</Heading>
                            <SelectBox
                                shape="round"
                                name="status"
                                options={dropDownOptions}
                                value={formData.status}
                                className="self-stretch"
                                onChange={handleSelectChange}
                            />
                            {/* Outros campos de formulário */}
                            <Button shape="round" className="mt-[82px] min-w-[156px] font-semibold sm:px-5" onClick={handleSubmit}>
                                Salvar
                            </Button>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}
